#!/bin/shfrom selenium import webdriverfrom selenium.webdriver.chrome.service import Serviceimport timeimport jsonimport osfrom selenium.webdriver.common.by import Byfrom selenium.webdriver.chrome.options import Options# 虚拟出Chrome界面chrome_options = Options()chrome_options = webdriver.ChromeOptions()chrome_options.add_argument('--no-sandbox')chrome_options.add_argument('--window-size=1420,1080')chrome_options.add_argument('--headless')chrome_options.add_argument('--disable-gpu')# action  linux服务器驱动地址ser = Service('chromedriver')driver = webdriver.Chrome(options=chrome_options)# driver = webdriver.Chrome(executable_path='/home/runner/work/Neworld_SignIn/Neworld_SignIn/driver/chromedriver')    # Chrome浏览器  # windows 系统驱动路径# driver = webdriver.Chrome(executable_path='D:\Downloads\chromedriver_win32\chromedriver.exe')    # Chrome浏览器# 环境变量中读取数据，包含账号密码，和登陆页面测试u = os.environ["USERNAME"]p = os.environ["PASSWORD"]print('u',u)print('p',p)driver.get("https://neworld.tv/auth/login") #  获取cookies time.sleep(5)# 账号密码登录版本driver.find_element(By.ID, 'email').clear()driver.find_element(By.ID, "email").send_keys(u)driver.find_element(By.ID, 'passwd').clear()driver.find_element(By.ID, "passwd").send_keys(p)time.sleep(1)driver.find_element(By.ID, "login").click()driver.refresh()#刷新页面 driver.refresh()#刷新页面 # time.sleep(5)# buttons = driver.find_element_by_xpath("//button[@id='checkin']")# print('buttons',buttons)driver.find_element(By.ID, "checkin").click() # 点击元素